import React, { useEffect, useState } from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import axios from 'axios'

const containerStyle = {
    width: '100%',
    height: '100vh',
};

const center = {
    lat: -3.745,
    lng: -38.523
};

function MapComponent() {
    console.log('kimchi2')
    const [position, setPosition] = useState({
        lat: -3.745,
        lng: -38.523
    })
    const [getMessage, setGetMessage] = useState({})
    const [boundaries, setBoundaries] = useState({
        northEastLat: null,
        northEastLng: null,
        southWestLat: null,
        southWestLng: null
    })
    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyCuqNmuK8YHJn06WZwIkzZd2J8_XTJxpxM"
    })

    const mapRef= React.useRef(null);
    const [getAllPositions, setAllPositions] = useState();
    const [waypoints, setWayPoints] = React.useState([]);



    const onTilesLoaded = React.useCallback(async function callback(map) {


        navigator.geolocation.getCurrentPosition((position) => {
            setPosition({ lat: position.coords.latitude, lng: position.coords.longitude });
            // doSomething(position.coords.latitude, position.coords.longitude);
            // this should be backend call
        });

        const bounds = mapRef.current.getBounds();
        console.log('kimchi5')
        console.log(mapRef)

        let boundaries = {
            northEastLat: bounds.getNorthEast().lat(),
            northEastLng: bounds.getNorthEast().lng(),
            southWestLat: bounds.getSouthWest().lat(),
            southWestLng: bounds.getSouthWest().lng()
        }
        setBoundaries(boundaries);
        console.log('kimchi 4' + boundaries.northEastLng);
        // await axios.post('http://localhost:5000/geocache', boundaries).then(response => {
        //     console.log("SUCCESS", response)
        //     setAllPositions(response)
        // }).catch(error => {
        //     console.log(error)
        // })

        await axios.get(`http://localhost:5000/geocache/${boundaries.northEastLat}/${boundaries.northEastLng}/${boundaries.southWestLat}/${boundaries.southWestLng}`).then(response => {
            console.log("SUCCESSGET", response)
            setGetMessage(response)
            setWayPoints(response.data.message);
            console.log(response.data.message)
        }).catch(error => {
            console.log(error)
        })

        console.log(waypoints)

        console.log('kimin called')
    }, [setWayPoints])

    const onLoad = React.useCallback(async function callback(map) {

        // This is just an example of getting and using the map instance!!! don't just blindly copy!
        navigator.geolocation.getCurrentPosition((position) => {
            setPosition({ lat: position.coords.latitude, lng: position.coords.longitude });
            // doSomething(position.coords.latitude, position.coords.longitude);
            // this should be backend call
        });
        const bounds = new window.google.maps.LatLngBounds(position);
        console.log('kimchi')

        let boundaries = {
            northEastLat: bounds.getNorthEast().lat(),
            northEastLng: bounds.getNorthEast().lng(),
            southWestLat: bounds.getSouthWest().lat(),
            southWestLng: bounds.getSouthWest().lng()
        }
        setBoundaries(boundaries);
        console.log('kimchi 4' + boundaries.northEastLng);
        // await axios.post('http://localhost:5000/geocache', boundaries).then(response => {
        //     console.log("SUCCESS", response)
        //     setAllPositions(response)
        // }).catch(error => {
        //     console.log(error)
        // })

        await axios.get(`http://localhost:5000/geocache/${boundaries.northEastLat}/${boundaries.northEastLng}/${boundaries.southWestLat}/${boundaries.southWestLng}`).then(response => {
            console.log("SUCCESSGET", response)
            setGetMessage(response)
        }).catch(error => {
            console.log(error)
        })
        console.log('successget ' + getMessage.data);

        map.fitBounds(bounds);

        mapRef.current = map;
        console.log('kimin6')
        console.log(map.getBounds())
    }, [])

    const onUnmount = React.useCallback(function callback(map) {
        mapRef.current = null;
    }, [])

    return isLoaded ? (
        <GoogleMap
            mapContainerStyle={containerStyle}
            center={position}
            zoom={10}
            onLoad={onLoad}
            onUnmount={onUnmount}
            onTilesLoaded={onTilesLoaded}
        // onBoundsChanged={onChange}
        >
            { /* Child components, such as markers, info windows, etc. */}
            {/* {getMessage.map(waypoint => {
                return <Marker key={waypoint.name} position={waypoint.position} clickable={true}/>;
            })} */}
            {<Marker position={position} clickable={true} />}
        </GoogleMap>
    ) : <></>
}

export default React.memo(MapComponent)
